// ----
// DOMs
// ----
const cardContainer = document.querySelector(".card-container"),
  cardItems = Array.from(document.querySelectorAll(".card-item")),
  // MovieDesBox↓
  movieDesBox = document.querySelector(".movie-des-box"),
  movieDesImg = movieDesBox.querySelector("img"),
  movieDesTxt = movieDesBox.querySelector("h4"),
  movieDesCloseBtn = movieDesBox.querySelector("span"),
  // search contents↓
  searchResultBox = document.querySelector(".search-results-box"),
  searchBox = document.getElementById("searchBox"),
  searchBtn = document.getElementById("searchBtn"),
  // bottom contents↓
  bottomContainer = document.querySelector(".bottom-container"),
  phaseContents = document.querySelectorAll(".bottom-container ul li");

let deviceCheck = false,
  isDragging = false,
  startPos = 0,
  currentTranslate = 0,
  prevTranslate = 0,
  animationID = 0,
  currentIndex = 0,
  movieObjNum = 0,
  cardLength,
  isDesMenu = false,
  cItems = document.querySelectorAll(".card-item");

// -------
// Methods
// -------

// Check the Device
function checkTheDevice() {
  if (
    /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
      navigator.userAgent
    )
  ) {
    // true for mobile device
    deviceCheck = true;
  } else {
    // false for not mobile device
    deviceCheck = false;
  }
}

// Add Obj as Des
function showMovieDes() {
  movieDesBox.classList.add("show");
  movieDesTxt.innerText = movieDesObj[currentIndex].des;
  movieDesImg.src = movieDesObj[currentIndex].img;
  setTimeout(function () {
    isDesMenu = true;
  }, 10);
}
function hideMovieDes() {
  movieDesBox.classList.remove("show");
  isDesMenu = false;
}
function checkHideBtnClick(e) {
  if (isDesMenu) {
    if (!e.target.closest(".movie-des-box")) {
      hideMovieDes();
    }
  }
}
function detectDesMenuMoving() {
  const currentImg = cardItems[currentIndex].querySelector("img");
  const properPosition =
    window.innerWidth -
    (currentImg.getBoundingClientRect().left +
      currentImg.getBoundingClientRect().right);

  if (-50 < properPosition && 50 > properPosition) {
    showMovieDes();
  }
}

// Touch/Mouse Events
function loadCardItems() {
  cardItems.forEach(function (cardItem, index) {
    const cardImg = cardItem.querySelector("img");
    cardImg.addEventListener("dragstart", function (e) {
      e.preventDefault();
    });
    deviceCheck
      ? cardImg.addEventListener("click", showMovieDes)
      : cardImg.addEventListener("mouseup", detectDesMenuMoving);

    if (deviceCheck) {
      // touch event
      cardItem.addEventListener("touchstart", touchStart(index));
      cardItem.addEventListener("touchend", touchEnd);
      cardItem.addEventListener("touchmove", touchMove);
    } else {
      // Mouse events
      cardItem.addEventListener("mousedown", touchStart(index));
      cardItem.addEventListener("mouseup", touchEnd);
      cardItem.addEventListener("mouseleave", touchEnd);
      cardItem.addEventListener("mousemove", touchMove);
    }
  });
}

// Start
function touchStart(index) {
  return function (event) {
    currentIndex = index;
    startPos = getPositionX(event);
    isDragging = true;

    animationID = requestAnimationFrame(animation);
    cardContainer.classList.add("grabbing");
  };
}
// Move
function touchMove(event) {
  if (isDragging) {
    const currentPosition = getPositionX(event);
    currentTranslate = prevTranslate + currentPosition - startPos;
    isDesMenu = false;
  }
}
// End
function touchEnd() {
  isDragging = false;
  cancelAnimationFrame(animationID);

  const movedBy = currentTranslate - prevTranslate;
  if (movedBy < -100 && currentIndex < cardItems.length - 1) {
    currentIndex++;
    checkCurrentPhase();
  }
  if (movedBy > 100 && currentIndex > 0) {
    currentIndex--;
    checkCurrentPhase();
  }
  setPositionByIndex();
  cardContainer.classList.remove("grabbing");
}

// For Touch/Mouse Events
function hideDesMenuInClickEnd() {
  movieDesBox.classList.remove("show");
}

function getPositionX(event) {
  return !event.type.includes("mouse") ? event.touches[0].clientX : event.pageX;
}

function animation() {
  setCardItemPosition();
  if (isDragging) {
    requestAnimationFrame(animation);
  }
}
function setPositionByIndex() {
  currentTranslate = currentIndex * -window.innerWidth;
  prevTranslate = currentTranslate;
  setCardItemPosition();
}

function setCardItemPosition() {
  cardContainer.style.transform = "translateX(" + currentTranslate + "px)";
}

// ---------------
// Search Box
// ---------------
function showSearchResults(e) {
  e.preventDefault();
  cItems = [].slice.call(cItems);
  searchResultBox.innerHTML = "";
  document.body.classList.add("show-search-results");
  searchResultBox.classList.add("search-results-show");
  cardContainer.classList.add("ma-hide");
  bottomContainer.classList.add("ma-hide");

  const currentTxt = searchBox.value.toLowerCase().trim();
  let foundItems = [];
  cardItems.forEach((cardItem) => {
    const cardTitle = cardItem.firstElementChild.innerText.toLowerCase().trim();
    if (cardTitle.indexOf(currentTxt) !== -1) {
      const cloned_cardItem = cardItem.cloneNode(true);
      searchResultBox.appendChild(cloned_cardItem);
      const cardNum = cItems.indexOf(cardItem);
      foundItems.push(cloned_cardItem);
      foundItems.push(cardNum);
    }
  });
  if (!currentTxt.length) {
    resetCardItems();
    return;
  }
  foundItems.length > 0
    ? loadClonedCardItems(foundItems)
    : (searchResultBox.innerHTML = "<h5>Not Found</h5>");
}
let cardArr;
let numArr;
function loadClonedCardItems(array) {
  cardArr = array.filter(function (item, index) {
    return index % 2 === 0;
  });
  numArr = array.filter(function (item, index) {
    return index % 2 === 1;
  });

  for (let i = 0; i < cardArr.length; i++) {
    const cloneImg = cardArr[i].querySelector("img");
    deviceCheck
      ? cloneImg.addEventListener("click", setCurrentIndex)
      : cloneImg.addEventListener("mouseup", setCurrentIndex);
  }
  function setCurrentIndex(e) {
    const targetCard = e.target.parentNode;
    const currentNum = cardArr.indexOf(targetCard);
    currentIndex = numArr[currentNum];
    showMovieDes();
  }
}
function resetCardItems() {
  document.body.classList.remove("show-search-results");
  searchResultBox.classList.remove("search-results-show");
  cardContainer.classList.remove("ma-hide");
  bottomContainer.classList.remove("ma-hide");
}

// For Phases Box
function checkCurrentPhase() {
  if (currentIndex < 6) {
    deleteClassFromAllPhases();
    phaseContents[0].classList.add("current-phase");
    return;
  } else if (currentIndex >= 6 && currentIndex < 12) {
    deleteClassFromAllPhases();
    phaseContents[1].classList.add("current-phase");
    return;
  } else if (currentIndex >= 12 && currentIndex < 23) {
    deleteClassFromAllPhases();
    phaseContents[2].classList.add("current-phase");
    return;
  } else if (currentIndex >= 23) {
    deleteClassFromAllPhases();
    phaseContents[3].classList.add("current-phase");
    return;
  }
}

function deleteClassFromAllPhases() {
  for (let i = 0; i < phaseContents.length; i++) {
    const phaseContent = phaseContents[i];
    phaseContent.classList.remove("current-phase");
  }
}

function goToThePhase(e) {
  const targetPhaseTxt = e.target.innerText;
  if (targetPhaseTxt === "Phase 1") {
    currentIndex = 0;
    currentTranslate = currentIndex;
  } else if (targetPhaseTxt === "Phase 2") {
    currentIndex = 6;
    currentTranslate = -innerWidth * currentIndex;
  } else if (targetPhaseTxt === "Phase 3") {
    currentIndex = 12;
    currentTranslate = -innerWidth * currentIndex;
  } else {
    currentIndex = 23;
    currentTranslate = -innerWidth * currentIndex;
  }
  prevTranslate = currentTranslate;
  setCardItemPosition();
  checkCurrentPhase();
}

